        os.system('del /f *.stt')
        os.system('del /f *.exception')
        os.system('del /f fort*')
        os.system('del /f *.dbg')
        os.system('del /f abaqus_acis.log')
        os.system('del /f abq.app_cache')
        os.system('del /f abaqus.rpt')
